# Module for colors
class Colors:
    def __init__(self):
        self.BLACK = (0, 0, 0)
        self.WHITE = (255, 255, 255)

        self.DARK_GREY = (32, 32, 32)
        self.GREY = (64, 64, 64)
        self.LIGHT_GREY = (128, 128, 128)

        self.RED = (255, 0, 0)
        self.GREEN = (0, 255, 0)
        self.BLUE = (0, 0, 255)

        self.LIGHT_RED = (255, 64, 64)
        self.LIGHT_GREEN = (64, 255, 64)
        self.LIGHT_BLUE = (64, 64, 255)

        self.YELLOW = (255, 255, 0)
        self.CYAN = (0, 255, 255)
        self.PINK = (255, 0, 255)
